#!/usr/bin/env bash
gcloud beta container --project "decent-tracer-255319" clusters create "castanddogs" --zone "europe-west1-b" --no-enable-basic-auth --release-channel "rapid" --machine-type "g1-small" --image-type "COS" --disk-type "pd-standard" --disk-size "10" --metadata disable-legacy-endpoints=true --scopes "https://www.googleapis.com/auth/devstorage.read_only","https://www.googleapis.com/auth/logging.write","https://www.googleapis.com/auth/monitoring","https://www.googleapis.com/auth/servicecontrol","https://www.googleapis.com/auth/service.management.readonly","https://www.googleapis.com/auth/trace.append" --preemptible --num-nodes "3" --no-enable-cloud-logging --no-enable-cloud-monitoring --enable-ip-alias --network "projects/decent-tracer-255319/global/networks/default" --subnetwork "projects/decent-tracer-255319/regions/europe-west1/subnetworks/default" --default-max-pods-per-node "110" --addons HorizontalPodAutoscaling,Istio --istio-config auth=MTLS_PERMISSIVE --enable-autoupgrade --enable-autorepair --no-shielded-integrity-monitoring
